mapper.update:
UPDATE tb_shop
shop_name=#{shopName}, shop_desc=#{shopDesc}, shop_addr=#{shopAddr}, phone=#{phone}, shop_img=#{shopImg}, priority=#{priority}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus}, advice=#{advice}, shop_category_id=#{shopCategory.shopCategoryId}, area_id=#{area.areaId}
WHERE shop_id = #{shopId}

mapper.select:
SELECT s.shop_id, s.shop_name, s.shop_desc, s.shop_addr, s.phone, s.shop_img, s.priority, s.create_time, s.last_edit_time, s.enable_status, s.advice, a.area_id, a.area_name, sc.shop_category_id, sc.shop_category_name FROM tb_shop s, tb_area a, tb_shop_category sc
and s.shop_category_id = #{shopCondition.shopCategory.shopCategoryId}
and s.shop_category_id in (select shop_category_id from tb_shop_category where parent_id = #{shopCondition.shopCategory.parent.shopCategoryId})
and s.area_id = #{shopCondition.area.areaId}
and s.shop_name like '%${shopCondition.shopName}%'
and s.enable_status = #{shopCondition.enableStatus}
and s.owner_id = #{shopCondition.owner.userId}
AND s.area_id = a.area_id AND s.shop_category_id = sc.shop_category_id
ORDER BY priority DESC LIMIT #{rowIndex},#{pageSize};

SELECT
count(1)
FROM
tb_shop s, tb_area a, tb_shop_category sc


		and s.shop_category_id = #{shopCondition.shopCategory.shopCategoryId}


		and s.area_id = #{shopCondition.area.areaId}



		and s.shop_name like '%${shopCondition.shopName}%'


		and s.enable_status = #{shopCondition.enableStatus}


		and s.owner_id = #{shopCondition.owner.userId}

	AND
	s.area_id = a.area_id
	AND
	s.shop_category_id = sc.shop_category_id
